        } else {
            permissionLauncher.launch(permissions.toArray(new String[0]));
        }
    }

    private void handlePhotoOperation() {
        if (btnTakePhoto.isPressed()) {
            launchCamera();
        } else {
            openDocumentLauncher.launch(new String[]{"image/*"});
        }
    }

