    private void logoutUser() {
        apiClient.logout();
        Intent intent = new Intent(MainActivity.this, LoginActivity.class);
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    @Override
    public void onBackPressed() {
        if (drawerLayout.isDrawerOpen(GravityCompat.START)) {
            drawerLayout.closeDrawer(GravityCompat.START);
        } else {
            super.onBackPressed();
        }
    }

    @Override
    protected void onResume() {
        super.onResume();
        updateNavigationHeader();
        // Ensure our custom parents exist even after configuration/activity changes
        if (navigationView != null) {
            Menu m = navigationView.getMenu();
            if (m == null || m.findItem(R.id.nav_terms_menu) == null || m.findItem(R.id.nav_privacy_menu) == null) {
                Log.d(TAG, "Rebuilding drawer menu in onResume");
                rebuildDrawerMenu();
                setTermsChildrenVisible(false);
                setPrivacyChildrenVisible(false);
            }
        }
    }
}

package com.example.ph906_spalshscreen;

import android.content.Intent;
import android.os.Bundle;

import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;

import com.example.ph906_spalshscreen.ui.privacy.DataPrivacyAdultFragment;
import com.example.ph906_spalshscreen.ui.privacy.DataPrivacyMinorFragment;

public class PrivacyActivity extends AppCompatActivity {

    private String version;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_privacy);

        version = getIntent().getStringExtra("version");
        if (version == null || version.trim().isEmpty()) version = "minor";

        Fragment fragment = "minor".equals(version)
                ? new DataPrivacyMinorFragment()
                : new DataPrivacyAdultFragment();

        getSupportFragmentManager().beginTransaction()
                .replace(R.id.privacy_container, fragment)
                .commit();
    }

    // Called by your fragment when user taps "Agree"
    public void onAgreed() {
        new PrefManager(this).acceptPrivacy(version, true);

        startActivity(new Intent(this, MainActivity.class)
                .setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK));
        finish();
    }
}

