            }

            // Create a unique filename using timestamp
            String timeStamp = new SimpleDateFormat("yyyyMMdd_HHmmss", Locale.US).format(new Date());
            File photoFile = new File(picturesDir, "IMG_" + timeStamp + ".jpg");

            // Get URI using FileProvider
            cameraTempUri = FileProvider.getUriForFile(this,
                    getPackageName() + ".fileprovider",
                    photoFile);

            takePictureLauncher.launch(cameraTempUri);
        } catch (Exception e) {
            Toast.makeText(this, "Camera error: " + e.getMessage(), Toast.LENGTH_LONG).show();
        }
    }

    private String formatPh906(String raw) {
        String digits = raw == null ? "" : raw.replaceAll("[^0-9]", "");
        return "PH906-" + digits;
    }

    private void saveAllChanges() {
        if (isPhotoChanged && pendingPhotoUri != null) {
            // Upload photo first, then save profile
            btnSave.setEnabled(false); // Prevent double-submission
            Toast.makeText(this, "Uploading photo...", Toast.LENGTH_SHORT).show();

            apiClient.uploadProfilePhoto(pendingPhotoUri, new ApiCallback() {
                @Override
                public void onSuccess(JSONObject response) {
                    // Save the photo URL from response
                    String photoUrl = response.optString("url");
                    if (!photoUrl.isEmpty()) {
                        prefsHelper.saveServerPhotoUrl(photoUrl);

                        // Show the server image now (bypass cache once)
                        Glide.with(EditProfileActivity.this)
                                .load(photoUrl + (photoUrl.contains("?") ? "&" : "?") + "t=" + System.currentTimeMillis())
                                .placeholder(R.drawable.account_circle)
                                .error(R.drawable.account_circle)
                                .into(imgProfileEdit);
                    }

                    // Now save the rest of the profile
                    runOnUiThread(() -> {
                        Toast.makeText(EditProfileActivity.this, "Photo uploaded", Toast.LENGTH_SHORT).show();
                        saveProfile();
                    });
                }

                @Override
                public void onError(String message) {
                    runOnUiThread(() -> {
                        Toast.makeText(EditProfileActivity.this,
                                "Photo upload failed: " + message, Toast.LENGTH_LONG).show();
                        btnSave.setEnabled(true);
                        // Proceed with saving other changes anyway
                        saveProfile();
                    });
                }
            });
        } else {
            // No photo change, just save profile
            saveProfile();
        }
    }

    private void saveProfile() {
        try {
            JSONObject payload = new JSONObject();
            payload.put("first_name", etFirstName.getText().toString().trim());
            payload.put("last_name", etLastName.getText().toString().trim());
            payload.put("sex", etSex.getText().toString().trim());
            payload.put("birthday", etBirthdate.getText().toString().trim());
            payload.put("age", etAge.getText().toString().trim());
            payload.put("caseworker_assigned", etCaseworkerAssigned.getText().toString().trim());
            payload.put("teacher", etTeacher.getText().toString().trim());
            payload.put("nickname", etNickname.getText().toString().trim());
            payload.put("mobile_number", etMobileNumber.getText().toString().trim());
            payload.put("email", etEmail.getText().toString().trim());
            payload.put("address", etAddress.getText().toString().trim());
            payload.put("guardian_name", etGuardianName.getText().toString().trim());
            payload.put("guardian_mobile", etGuardianMobile.getText().toString().trim());
            payload.put("water_baptized", etWaterBaptized.getText().toString().trim());

            btnSave.setEnabled(false);

            // Route through ApiClient (which has endpoint fallback logic)
            apiClient.updateMyProfile(payload, new ApiCallback() {
                @Override
                public void onSuccess(JSONObject response) {
                    runOnUiThread(() -> {
                        Toast.makeText(EditProfileActivity.this, "Profile updated", Toast.LENGTH_SHORT).show();
                        setResult(RESULT_OK);
                        finish();
                    });
                }

                @Override
                public void onError(String message) {
                    runOnUiThread(() -> {
                        Toast.makeText(EditProfileActivity.this, "Save failed: " + message, Toast.LENGTH_LONG).show();
                        btnSave.setEnabled(true);
                    });
                }
            });
        } catch (JSONException e) {
            Toast.makeText(this, "Failed to prepare data: " + e.getMessage(), Toast.LENGTH_SHORT).show();
            btnSave.setEnabled(true);
        }
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        if (queue != null) {
            queue.cancelAll(request -> true);
        }
    }
}

package com.example.ph906_spalshscreen.ui.profile;

import android.app.Activity;
import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;
import android.widget.Toast;

import androidx.activity.result.ActivityResultLauncher;
import androidx.activity.result.contract.ActivityResultContracts;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import com.bumptech.glide.Glide;
import com.bumptech.glide.load.engine.DiskCacheStrategy;
import com.example.ph906_spalshscreen.PrefsHelper;
import com.example.ph906_spalshscreen.R;
import com.example.ph906_spalshscreen.api.ApiCallback;
import com.example.ph906_spalshscreen.api.ApiClient;

import org.json.JSONObject;

public class ProfileFragment extends Fragment {

    private TextView tvUserId, tvFirstName, tvLastName, tvBirthdate, tvNickname,
            tvMobile, tvAddress, tvGuardian, tvGuardianMobile, tvBaptized, tvTeacher,
            tvSex, tvCaseworker, tvAge, tvEmail;
    private Button btnEdit;
    private ImageView imgProfile;

    private String ph906Raw;
    private ApiClient apiClient;
    private PrefsHelper prefs;

    private static final String BASE = "https://hjcdc.swuitapp.com"; // for normalizing relative URLs

    private final ActivityResultLauncher<Intent> editLauncher =
            registerForActivityResult(new ActivityResultContracts.StartActivityForResult(), result -> {
                if (result.getResultCode() == Activity.RESULT_OK && getContext() != null) {
                    PrefsHelper prefs = new PrefsHelper(requireContext());
                    String photoUri = prefs.getProfilePhotoUri();
                    if (photoUri != null) {
                        try { imgProfile.setImageURI(Uri.parse(photoUri)); } catch (Exception ignored) {}
                    }
                    loadProfile();
                }
            });

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,
                             @Nullable Bundle savedInstanceState) {
        View v = inflater.inflate(R.layout.fragment_profile, container, false);

        imgProfile = v.findViewById(R.id.img_profile);
        tvUserId = v.findViewById(R.id.tv_user_id);
        tvFirstName = v.findViewById(R.id.tv_first_name);
        tvLastName = v.findViewById(R.id.tv_last_name);
        tvBirthdate = v.findViewById(R.id.tv_birthdate);
        tvNickname = v.findViewById(R.id.tv_nickname);
        tvMobile = v.findViewById(R.id.tv_mobile);
        tvEmail = v.findViewById(R.id.tv_email);
        tvAddress = v.findViewById(R.id.tv_address);
        tvGuardian = v.findViewById(R.id.tv_guardian);
        tvGuardianMobile = v.findViewById(R.id.tv_guardian_mobile);
        tvBaptized = v.findViewById(R.id.tv_baptized);
        tvTeacher = v.findViewById(R.id.tv_teacher);
        tvSex = v.findViewById(R.id.tv_sex);
        tvCaseworker = v.findViewById(R.id.tv_caseworker);
        tvAge = v.findViewById(R.id.tv_age);
        btnEdit = v.findViewById(R.id.btn_edit);

        apiClient = new ApiClient(requireContext());
        prefs = new PrefsHelper(requireContext());

        ph906Raw = prefs.getPh906();
        set(tvUserId, formatPh906(ph906Raw));

        String savedUrl = prefs.getProfilePhotoUri();
        if (savedUrl != null && !savedUrl.isEmpty()) {
            loadProfileImage(savedUrl, false);
        } else {
            Glide.with(this).load(R.drawable.account_circle).into(imgProfile);
        }

        btnEdit.setOnClickListener(view -> {
            Intent intent = new Intent(getActivity(), EditProfileActivity.class);
            intent.putExtra("ph906Id", ph906Raw);
            // Pass current visible values to avoid clearing in EditProfileActivity
            intent.putExtra("firstName", safe(tvFirstName));
            intent.putExtra("lastName", safe(tvLastName));
            intent.putExtra("birthdate", safe(tvBirthdate));
            intent.putExtra("nickname", safe(tvNickname));
            intent.putExtra("mobile", safe(tvMobile));
            intent.putExtra("email", safe(tvEmail));
            intent.putExtra("address", safe(tvAddress));
            intent.putExtra("guardian", safe(tvGuardian));
            intent.putExtra("guardianMobile", safe(tvGuardianMobile));
            intent.putExtra("baptized", safe(tvBaptized));
            intent.putExtra("teacher", safe(tvTeacher));
            intent.putExtra("sex", safe(tvSex));
            intent.putExtra("age", safe(tvAge));
            intent.putExtra("caseworker", safe(tvCaseworker));
            editLauncher.launch(intent);
        });

        loadProfile();
        return v;
    }

    private String formatPh906(String raw) {
        if (raw == null || raw.isEmpty()) return "PH906-";
        String digits = raw.replaceAll("[^0-9]", "");
        if (digits.length() >= 1) return "PH906-" + digits;
        return raw.startsWith("PH906-") ? raw : ("PH906-" + raw);
    }

    private void loadProfile() {
        apiClient.getMyProfile(new ApiCallback() {
            @Override
            public void onSuccess(JSONObject response) {
                if (!isAdded()) return;
                requireActivity().runOnUiThread(() -> handleProfileResponse(response));
            }
            @Override
            public void onError(String message) {
                if (!isAdded()) return;
                requireActivity().runOnUiThread(() ->
                        Toast.makeText(getContext(), "Load failed: " + message, Toast.LENGTH_LONG).show()
                );
            }
        });
    }

    private void handleProfileResponse(JSONObject response) {
        try {
            JSONObject obj = response;
            if (response.has("data") && response.opt("data") instanceof JSONObject) {
                obj = response.getJSONObject("data");
            }
            // ALWAYS coerce to String; never pass numbers directly to setText(int)
            set(tvFirstName, obj.optString("first_name", ""));
            set(tvLastName,  obj.optString("last_name", ""));
            set(tvBirthdate, obj.optString("birthday", ""));
            set(tvNickname,  obj.optString("nickname", ""));

            String mobile = firstNonEmpty(obj.optString("mobile_number", ""), obj.optString("mobile", ""));
            set(tvMobile, mobile);

            set(tvEmail, obj.optString("email", ""));
            set(tvAddress,  obj.optString("address", ""));
            set(tvGuardian, obj.optString("guardian_name", ""));
            set(tvGuardianMobile, obj.optString("guardian_mobile", ""));
            set(tvBaptized, firstNonEmpty(obj.optString("water_baptized", ""), obj.optString("baptized", "")));
            set(tvTeacher,  obj.optString("teacher", ""));
            set(tvSex,      obj.optString("sex", ""));
            // age can be int or string; convert safely
            String ageStr = obj.has("age") ? String.valueOf(obj.opt("age")) : "";
            set(tvAge, "null".equalsIgnoreCase(ageStr) ? "" : ageStr);
            set(tvCaseworker, obj.optString("caseworker_assigned", ""));

            // If backend includes photo_url, prefer it and save
            String apiPhotoUrl = obj.optString("photo_url", "").trim();
            if (!apiPhotoUrl.isEmpty()) {
                String normalized = normalizeUrl(apiPhotoUrl);
                prefs.saveProfilePhotoUri(normalized);
                loadProfileImage(normalized, true); // cache-bust once
            }
        } catch (Exception e) {
            Toast.makeText(getContext(), "Parse error: " + e.getMessage(), Toast.LENGTH_LONG).show();
        }
    }

    private String normalizeUrl(String url) {
        if (url == null) return null;
        String u = url.trim();
        if (u.startsWith("http://") || u.startsWith("https://")) return u;
        // If it already includes /api/, keep it; else prepend base + "/api"
        if (u.startsWith("/")) {
            return (u.startsWith("/api/") ? (BASE + u) : (BASE + "/api" + u));
        }
        return BASE + "/api/" + u;
    }

    private String firstNonEmpty(String a, String b) {
        if (a != null && !a.trim().isEmpty()) return a;
        return (b == null) ? "" : b;
    }

    private void loadProfileImage(String url, boolean bustCache) {
        if (!isAdded()) return;
        String normalized = normalizeUrl(url);
        String toLoad = normalized;
        if (bustCache) {
            String sep = normalized.contains("?") ? "&" : "?";
            toLoad = normalized + sep + "t=" + System.currentTimeMillis();
        }
        // Clear any pending request to avoid flicker/race
        try { Glide.with(this).clear(imgProfile); } catch (Exception ignored) {}

        try {
            Glide.with(this)
                    .load(toLoad)
                    .placeholder(R.drawable.account_circle)
                    .error(R.drawable.account_circle)
                    .centerCrop()
                    .diskCacheStrategy(bustCache ? DiskCacheStrategy.NONE : DiskCacheStrategy.AUTOMATIC)
                    .skipMemoryCache(bustCache)
                    .into(imgProfile);
        } catch (IllegalStateException ignored) {
            // Fragment might be in transition; skip
        }
    }

    // Safe setter: coerce any value to string and coalesce nulls to ""
    private void set(TextView v, Object val) {
        if (v == null) return;
        String s;
        if (val == null) {
            s = "";
        } else {
            s = String.valueOf(val);
            if ("null".equalsIgnoreCase(s)) s = "";
        }
        v.setText(s);
    }

    private String safe(TextView tv) {
        return tv != null && tv.getText() != null ? tv.getText().toString() : "";
    }
}

